var RecordBulkProcessor = x_snc_devtools.RecordBulkProcessor;

function CallbackRecordProcessor(record)
{
   Log('Do something with the record');
   
   // Do not call record.update()!

   // return true if the record needs an update, otherwise false!
   return true; 
}
function CallbackLog(strText)
{
   x_snc_devtools.Log(strText);
}

var grRecords = new GlideRecord('x_snc_devtools_test');

RecordBulkProcessor(
   grRecords,
   CallbackRecordProcessor, // callback function to work on a single record
   'processed', // the name of the field that stores that date/time of the last processing
   1000, // maximum number of records to be processed
   5, // maximum number of seconds to run
   CallbackLog, // Callback function used for logging
   100 // Produce a status log output every n records
);
