		+'\n["sys_tags"]: '
		+'\n["sys_updated_by"]: '
		+'\n["sys_updated_on"]: '
		+'\n["value"]: Hello World'
		);
	
	TestCase_RenderValueHideEmpty(gr,
		'\nx_snc_devtools_test:'
		+'\n["name"]: Test'
		+'\n["sys_meta"]: null'
		+'\n["sys_mod_count"]: 0'
		+'\n["value"]: Hello World'
		);	

	
	// Nested GlideRecords
	TestCase_RenderValue(
		{
			'record1': gr
		},
		'\n["record1"]: '
		+'\n___x_snc_devtools_test:'
		+'\n___["name"]: Test'
