sys_variable_value.value:
var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;

	var bResult = true;

	function TestCase_SetProperty(vInputName, vInputValue,vExpected)
	{
		Log('TestCase_SetProperty');
		Log('Input name    : '+RenderValue(vInputName));
		Log('Input value   : '+RenderValue(vInputValue));
		Log('Expected      : '+RenderValue(vExpected));
		var vResult = SetProperty(vInputName,vInputValue);
		Log('Result        : '+RenderValue(vResult));
		if (vResult != vExpected)
		{
			bResult = false;
			Log('Test case FAILED');
		}
		if (vExpected == true)
		{
			var vPropertyValue = gs.getProperty(vInputName);
			Log('gs.getProperty: '+RenderValue(vPropertyValue));

			if (vInputValue != vPropertyValue)
			{
				bResult = false;
				Log('Test case FAILED');
			}
		}
		Log('');
	}
	
	// Non-existent properties 
	TestCase_SetProperty(false,'Hello World', false);
	TestCase_SetProperty('','Hello World',false);
	TestCase_SetProperty('this_property_does_not_exist','Hello World',false);
	
	// Same scope
	TestCase_SetProperty('x_snc_devtools.debug.log','true',true);
	
	// Global scope
	TestCase_SetProperty('sn_atf.runner.enabled','true',true);
	
	// Other scope
	TestCase_SetProperty('sn_codesearch.search.results.max','1000',true);

	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
