var strLog = '';
function Log(strText)
{
	strLog = strLog + "\n" + strText;
}
(function(outputs, steps, stepResult, assertEqual)
{
	var bResult = true;
	

	function TestCase_DatabaseView(vInputDbName, vInputDbView, bExpectedView, bExpectedItem)
	{
		Log('TestCase_DatabaseView');
		Log('bExpectedView     : '+RenderValue(bExpectedView));
		Log('bExpectedItem     : '+RenderValue(bExpectedItem));
		Log('vInputName        : '+RenderValue(vInputDbName));
		Log('vInputDbView      : '+RenderValue(vInputDbView));
		
		var testdata = new TestDataAPI('_ATF_DEVTOOLS_');	
		var strDbViewSysId = testdata.CreateDatabaseView(vInputDbName,vInputDbView);
		
		Log('DbViewSysId      : '+RenderValue(strDbViewSysId));
		if (bExpectedView == false && IsValidSysId(strDbViewSysId) == false)
		{
			Log('');
			return;
		}
		
		if (bExpectedView == false && IsValidSysId(strDbViewSysId))
		{
			Log('Test case FAILED');
			bResult = false;
			Log('');
			return;
			
		}

		
		var strName = 'TEST';
		var strValue = 'Hello World';
		var strTestItemSysId = testdata.CreateTestItem(strName,strValue);
		Log(RenderValue(testdata.GetAllData()));

		var grTest = new GlideRecord(testdata.GetDatabaseViewName(vInputDbName));
		grTest.addQuery('name','=',testdata.GetPrefix()+strName);
		grTest.query();
		if (bExpectedItem == true)
		{
			Log('Expecting to find the item and read the value!');
			if (grTest.getRowCount() != 1)
			{
				Log('Test case FAILED');
				bResult = false;	
			}
			else
			{
				grTest.next();
				Log(RenderValue(grTest));
				if (GetStringValue(grTest.getValue('test_value')) != strValue)
				{
					bResult = false;	
					Log('Test case FAILED');	
				}
			}
		}
		else
		{
			Log('Expecting to find the item but NOT to read the value!');
			if (grTest.getRowCount() != 1)
			{
				Log('Test case FAILED');
				bResult = false;	
			}
			else
			{
				grTest.next();
				Log(RenderValue(grTest));
				if (GetStringValue(grTest.getValue('test_value')) == strValue)
				{
					bResult = false;	
					Log('Test case FAILED');	
				}
			}
		}
		Log('');
	}
	

	Log('Missing definition');
	TestCase_DatabaseView(
		'TEST1',
		null,
		false,
		false);		
	
	
	Log('Incomplete definition');
	TestCase_DatabaseView(
		'TEST2',
		{},
		false,
		false);		
	
	
	Log('Single table without fields');
	TestCase_DatabaseView(
		'TEST3',
		{
			'tables':
				[
					{
						'table': 'x_snc_devtools_test',
						'variable_prefix': 'test',
						'order': 1
					}
				]
		
		},
		true,
		true);	

	
	Log('Single table with fields');
	TestCase_DatabaseView(
		'TEST4',
		{
			'tables':
				[
					{
						'table': 'x_snc_devtools_test',
						'variable_prefix': 'test',
						'order': 1,
						'where_clause': '',
						'fields': ['name','value','sys_id']
					}
				]
		
		},
		true,
		true);	

	Log('Single table with incomplete field set');
	TestCase_DatabaseView(
		'TEST5',
		{
			'tables':
				[
					{
						'table': 'x_snc_devtools_test',
						'variable_prefix': 'test',
						'order': 1,
						'where_clause': '',
						'fields': ['name','sys_id']
					}
				]
		
		},
		true,
		false);	
	
	
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
