sys_script_include.script:
var g_nCounter = 0;
function Debug(vValue)
{
	var bDebugLog = GetBoolValue(gs.getProperty('x_snc_devtools.debug.log'));
	if (bDebugLog == false)
	{
		return;
	}
	g_nCounter++;
	var strText = '#'+GetStringValue(g_nCounter)+' ';

	var bDebugLogTransactionRuntime = GetBoolValue(gs.getProperty('x_snc_devtools.debug.log.transaction_runtime'));
	if (bDebugLogTransactionRuntime == true)
	{
		strText += GetTransactionRuntime()+'ms ';
	}
	strText += GetStringValue(vValue);
	Log(strText);
}
