sys_script_include.script:
function IsTechnicalUser(strUser)
{
	var grUser = new GlideRecord('sys_user');
	grUser.addQuery('sys_id',strUser);
	grUser.addQuery('web_service_access_only','true');
	grUser.query();
	if (grUser.next() == true)
	{
		return true;
	}
	var grUser = new GlideRecord('sys_user');
	grUser.addQuery('user_name',strUser);
	grUser.addQuery('web_service_access_only','true');
	grUser.query();
	if (grUser.next() == true)
	{
		return true;
	}
	return false;
}
